mapper.insert:
		INSERT INTO TIMETABLE(NO, BRANCH_NO, WORK_START_TIME, WORK_END_TIME, OCCUP_NAME, OCCUP_COLOR, REGISTER_DATE)
		VALUES(TIMETABLE_NO_SEQ.NEXTVAL
				,#{branchNo}
                ,#{workStartTime}
                ,#{workEndTime}
				,#{occupName}
				,#{occupColor}
				,SYSDATE)

mapper.select:
SELECT
     NO AS TIMETABLE_NO
    ,BRANCH_NO
    ,WORK_START_TIME
    ,WORK_END_TIME
    ,OCCUP_NAME
    ,OCCUP_COLOR
    ,REGISTER_DATE
FROM TIMETABLE

    BRANCH_NO = #{branchNo}

        AND WORK_START_TIME = #{workStartTime}


        AND WORK_END_TIME = #{workEndTime}


        AND OCCUP_NAME = #{occupName}


        AND OCCUP_COLOR = #{occupColor}
